class PV_Portales_Divi_Tabs{constructor($){this.init($);}
init(){this._handleTabClick($);this._triggerClick($);}
_triggerClick(){let windowWidth=jQuery(window).width();if(windowWidth>768){jQuery('.pv-titulos-custom-tabs-wrapper').each(function(){jQuery(this).find('.pv-portales-titulo-custom-tab').first().trigger('click')});}}
_handleTabClick(){let self=this;jQuery('.pv-portales-front').on('click','.pv-portales-titulo-custom-tab',function(){console.log('click');jQuery(this).parents('.pv-portales-custom-tabs-main-wrapper').find('.pv-portales-titulo-custom-tab').removeClass('pv-custom-tab-active');jQuery(this).addClass('pv-custom-tab-active');self.openCustomTab(jQuery(this));});};openCustomTab(thisElement){let thisElementText=thisElement.text();if(thisElementText!==''&&jQuery(`[data-title="${thisElementText}"]`).length>0){thisElement.parents('.pv-portales-custom-tabs-main-wrapper').find('.pv_portales_custom_tabs_child').css({display:'none'});thisElement.parents('.pv-portales-custom-tabs-main-wrapper').find(`[data-title="${thisElementText}"]`).parents('.pv_portales_custom_tabs_child').css({opacity:0,display:'block'});thisElement.parents('.pv-portales-custom-tabs-main-wrapper').find(`[data-title="${thisElementText}"]`).parents('.pv_portales_custom_tabs_child').stop().animate({opacity:1},500)}}}
window.PV_Portales_Divi_Tabs=PV_Portales_Divi_Tabs;jQuery(document).ready(($)=>{new window.PV_Portales_Divi_Tabs($);});