class PV_Portales_Divi_Scroll_Cards{constructor($){this.init($);}
init($){this.cardsScrollModificador($);this.onWindowResize($);setTimeout(()=>{this.slickInit($);},100)}
cardsScrollModificador($){$('.pv-portales-custom-cards-slick-container-inner').each(function(){let parentSection=$(this).parents('.pv_portales_custom_scroll_cards').first();let parentWidth=parentSection.width();$(this).css({width:parentWidth+'px'});});}
onWindowResize($){$(window).resize(()=>{this.cardsScrollModificador($);});}
slickInit($){$('.pv-portales-custom-scroll-cards-wrapper').each(function(){let mathID=Math.floor(Math.random()*1000000000);$(this).attr('id','slick-'+mathID);let currentID='#'+$(this).attr('id');$(currentID+' .pv-portales-custom-cards-all-children-inner').slick({prevArrow:currentID+' .pv-scroll-cards-button-prev',nextArrow:currentID+' .pv-scroll-cards-button-next',infinite:false,});});}}
window.PV_Portales_Divi_Scroll_Cards=PV_Portales_Divi_Scroll_Cards;jQuery(document).ready(($)=>{new window.PV_Portales_Divi_Scroll_Cards($);});