class Onlife_General_Front_Scripts{constructor($){this.headerAnimation($);this.onlifeMobileMenu($);this.onlifeNoLink($);}
onlifeNoLink($){$('.onlife-no-link > a').on('click touch',function(e){e.preventDefault();});}
onlifeMainMenu($){$('.onlife-main-menu .menu-item-has-children').hover(function(){$(this).find('.sub-menu').css({display:'block'}).stop().animate({opacity:1});},function(){$(this).find('.sub-menu').stop().animate({opacity:0},function(){$(this).css({display:'none'});});});}
onlifeMobileMenu($){let displayMobileMenu=false;let displayMobileSubmenu=false;let allMenu=$('.onlife-all-menus-wrapper');let allMenuPos=allMenu.offset();let mobileIcon=$('.onlife-mobile-menu-icon');let mobileIconHeight=mobileIcon.outerHeight();let mobileMenu=$('.onlife-main-menu-mobile');let header=mobileMenu.parents('#onlife-main-header').first();let headerHeight=header.outerHeight();$('.onlife-main-menu-wrapper-mobile').css({zIndex:999,top:headerHeight-3});$('.onlife-mobile-menu-icon').on('click touch',function(){let distancia=$(document).scrollTop();headerHeight=header.outerHeight();allMenu=$('.onlife-all-menus-wrapper');allMenuPos=allMenu.offset();$('.onlife-main-menu-wrapper-mobile').css({zIndex:999,top:headerHeight-3});displayMobileMenu=!displayMobileMenu;if(displayMobileMenu){$('#onlife-main-header').stop().animate({backgroundColor:'#262e33'},300);$('.onlife-mobile-menu-icons-wrapper').stop().animate({rotate:180},{step:function(now,fx){$(this).css('transform','rotate('+now+'deg)');},duration:200});$('.onlife-opened-mobile-menu').stop().animate({opacity:1},300);$('.onlife-closed-mobile-menu').stop().animate({opacity:0},300);mobileMenu.css({display:'flex'});let mobileMenuHeight=mobileMenu.outerHeight();mobileMenu.css({display:'none'});mobileMenu.css({height:0});mobileMenu.css({display:'flex'}).stop().animate({height:mobileMenuHeight},400,function(){mobileMenu.css({height:''});});}
else{if(distancia<40){$('#onlife-main-header').stop().animate({backgroundColor:'transparent'},300);}
$('.onlife-mobile-menu-icons-wrapper').stop().animate({rotate:-180},{step:function(now,fx){$(this).css('transform','rotate('+now+'deg)');},duration:200});$('.onlife-opened-mobile-menu').stop().animate({opacity:0},300);$('.onlife-closed-mobile-menu').stop().animate({opacity:1},300);mobileMenu.stop().animate({height:0},400,function(){mobileMenu.css({display:'none',height:''})});}})
$('.onlife-main-menu-mobile .menu-item-has-children').on('click touch',function(){displayMobileSubmenu=!displayMobileSubmenu;let submenu=$(this).find('.sub-menu').first();let iconUp=$(this).find('.onlife-angle-icon-up');let iconDown=$(this).find('.onlife-angle-icon-down');submenu.css({display:'block'});let submenuHeight=submenu.outerHeight();if(displayMobileSubmenu){submenu.css({height:0});iconUp.css({display:'inline-block'});iconDown.css({display:'none'});submenu.stop().animate({height:submenuHeight},300);}
else{submenu.stop().animate({height:0},300,function(){submenu.css({display:'none',height:''});iconUp.css({display:'none'});iconDown.css({display:'inline-block'});});}});}
headerAnimation($){let currentAnimated=false;let distancia=$(document).scrollTop();setTimeout(function(){if(distancia>=40){$('#onlife-main-header').css({backgroundColor:'#262e33'});}},500);$(window).on('scroll',function(){distancia=$(document).scrollTop();if(distancia>=40){if(!currentAnimated){$('#onlife-main-header').stop().animate({backgroundColor:'#262e33'});}
currentAnimated=true;}else{if(currentAnimated){$('#onlife-main-header').stop().animate({backgroundColor:'transparent'});}
currentAnimated=false;}});}}
jQuery(document).ready(function($){new Onlife_General_Front_Scripts($);});